mapper.delete:
DELETE FROM business_collected
WHERE user_id = #{userId}
AND module_type = #{moduleType}
AND module_type_id = #{moduleTypeId}

mapper.select:
SELECT  FROM business_collected
WHERE user_id = #{userId}
AND module_type = #{moduleType}

ELECT  FROM business_collected
WHERE user_id = #{userId}
AND module_type = #{moduleType}
AND module_type_id = #{moduleTypeId}
